import { useState, useEffect } from 'react';
import { getWatchlist, addToWatchlist, markAsWatched, removeFromWatchlist } from '../services/api';
import { Link } from 'react-router-dom';

function Watchlist() {
  const [watchlist, setWatchlist] = useState([]);
  const [movieId, setMovieId] = useState(''); // State for adding a movie

  useEffect(() => {
    fetchWatchlist();
  }, []);

  const fetchWatchlist = async () => {
    const { data } = await getWatchlist();
    setWatchlist(data);
  };

  const handleAddToWatchlist = async () => {
    if (movieId) {
      await addToWatchlist({ movie_id: movieId });
      setMovieId('');
      fetchWatchlist();
    }
  };

  const handleMarkAsWatched = async (watchlistId) => {
    await markAsWatched(watchlistId);
    fetchWatchlist();
  };

  const handleRemove = async (watchlistId) => {
    await removeFromWatchlist(watchlistId);
    fetchWatchlist();
  };

  return (
    <div>
      <h2>My Watchlist</h2>
      <div>
        <input
          type="text"
          value={movieId}
          onChange={(e) => setMovieId(e.target.value)}
          placeholder="Enter Movie ID to add"
        />
        <button onClick={handleAddToWatchlist}>Add to Watchlist</button>
      </div>
      {watchlist.length === 0 ? (
        <p>No movies in watchlist</p>
      ) : (
        <div className="movie-grid">
          {watchlist.map((item) => (
            <div key={item.id} className="movie-card">
              <img
                src={item.movie.poster_path ? `/storage/${item.movie.poster_path}` : 'placeholder.jpg'}
                alt={item.movie.title}
              />
              <h3>{item.movie.title}</h3>
              <p>{item.movie.release_date}</p>
              <p>{item.watched ? 'Watched' : 'Not Watched'}</p>
              <Link to={`/movies/${item.movie.id}`}>Details</Link>
              {!item.watched && (
                <button onClick={() => handleMarkAsWatched(item.id)}>Mark as Watched</button>
              )}
              <button onClick={() => handleRemove(item.id)}>Remove</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Watchlist;
