import { useState } from 'react';

function SearchBar({ onSearch }) {
  const [search, setSearch] = useState('');
  const [genre, setGenre] = useState('');
  const [actor, setActor] = useState('');

  const handleSearch = () => {
    onSearch(search, genre, actor);
  };

  return (
    <div>
      <input type="text" value={search} onChange={(e) => setSearch(e.target.value)} placeholder="Search by title" />
      <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} placeholder="Filter by genre" />
      <input type="text" value={actor} onChange={(e) => setActor(e.target.value)} placeholder="Filter by actor" />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
