import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getActor, createActor, updateActor, deleteActor } from '../services/api';

function AdminActorForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [actor, setActor] = useState({ name: '', birthdate: '', bio: '', photo: null });

  useEffect(() => {
    if (id) {
      const fetchActor = async () => {
        try {
          const { data } = await getActor(id);
          setActor(data.actor);
        } catch (error) {
          console.error('Error fetching actor:', error);
        }
      };
      fetchActor();
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setActor({ ...actor, [name]: files ? files[0] : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(actor).forEach(([key, value]) => {
      if (value) formData.append(key, value);
    });

    try {
      if (id) {
        await updateActor(id, formData);
      } else {
        await createActor(formData);
      }
      navigate('/admin/actors');
    } catch (error) {
      console.error('Error saving actor:', error);
    }
  };

  const handleDelete = async () => {
    if (id) {
      try {
        await deleteActor(id);
        navigate('/admin/actors');
      } catch (error) {
        console.error('Error deleting actor:', error);
      }
    }
  };

  return (
    <div>
      <h2>{id ? 'Edit Actor' : 'Create Actor'}</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" value={actor.name} onChange={handleChange} placeholder="Name" required />
        <input type="date" name="birthdate" value={actor.birthdate} onChange={handleChange} placeholder="Birthdate" required />
        <textarea name="bio" value={actor

.bio} onChange={handleChange} placeholder="Biography" />
        <input type="file" name="photo" onChange={handleChange} accept="image/*" />
        <button type="submit">{id ? 'Update' : 'Create'}</button>
        {id && <button type="button" onClick={handleDelete}>Delete</button>}
      </form>
    </div>
  );
}

export default AdminActorForm;
