import { Link, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import api from '../services/api';

function Header() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const checkAdmin = async () => {
      try {
        const { data } = await api.get('/user');
        setIsAdmin(data.role === 'admin'); // Assumes user object has a role field
      } catch (error) {
        console.error('Error checking admin status:', error);
      }
    };
    if (isAuthenticated) checkAdmin();
  }, [isAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    setIsAdmin(false);
    navigate('/login');
  };

  return (
    <nav>
      <Link to="/">Home</Link>
      <Link to="/watchlist">Watchlist</Link>
      {isAuthenticated ? (
        <>
          <Link to="/profile">Profile</Link>
          {isAdmin && (
            <>
              <Link to="/admin/dashboard">Admin Dashboard</Link>
              <Link to="/admin/movies">Manage Movies</Link>
              <Link to="/admin/actors">Manage Actors</Link>
              <Link to="/admin/genres">Manage Genres</Link>
              <Link to="/admin/realisateurs">Manage Directors</Link>
            </>
          )}
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </>
      )}
    </nav>
  );
}

export default Header;
