import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api'; // Use generic api instance since realisateur-specific methods are not defined

function AdminRealisateurForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [realisateur, setRealisateur] = useState({ fistName: '', lastName: '', birthDate: '' });

  useEffect(() => {
    if (id) {
      const fetchRealisateur = async () => {
        try {
          const { data } = await api.get(`/realisateurs/${id}`);
          setRealisateur(data.realisateur);
        } catch (error) {
          console.error('Error fetching director:', error);
        }
      };
      fetchRealisateur();
    }
  }, [id]);

  const handleChange = (e) => {
    setRealisateur({ ...realisateur, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await api.put(`/admin/realisateurs/${id}`, realisateur);
      } else {
        await api.post('/admin/realisateurs', realisateur);
      }
      navigate('/admin/realisateurs');
    } catch (error) {
      console.error('Error saving director:', error);
    }
  };

  const handleDelete = async () => {
    if (id) {
      try {
        await api.delete(`/admin/realisateurs/${id}`);
        navigate('/admin/realisateurs');
      } catch (error) {
        console.error('Error deleting director:', error);
      }
    }
  };

  return (
    <div>
      <h2>{id ? 'Edit Director' : 'Create Director'}</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="fistName" value={realisateur.fistName} onChange={handleChange} placeholder="First Name" required />
        <input type="text" name="lastName" value={realisateur.lastName} onChange={handleChange} placeholder="Last Name" required />
        <input type="date" name="birthDate" value={realisateur.birthDate} onChange={handleChange} placeholder="Birth Date" required />
        <button type="submit">{id ? 'Update' : 'Create'}</button>
        {id && <button type="button" onClick={handleDelete}>Delete</button>}
      </form>
    </div>
  );
}

export default AdminRealisateurForm;
