import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getGenre, createGenre, updateGenre, deleteGenre } from '../services/api';

function AdminGenreForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [genre, setGenre] = useState({ name: '' });

  useEffect(() => {
    if (id) {
      const fetchGenre = async () => {
        try {
          const { data } = await getGenre(id);
          setGenre(data.genre);
        } catch (error) {
          console.error('Error fetching genre:', error);
        }
      };
      fetchGenre();
    }
  }, [id]);

  const handleChange = (e) => {
    setGenre({ ...genre, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await updateGenre(id, genre);
      } else {
        await createGenre(genre);
      }
      navigate('/admin/genres');
    } catch (error) {
      console.error('Error saving genre:', error);
    }
  };

  const handleDelete = async () => {
    if (id) {
      try {
        await deleteGenre(id);
        navigate('/admin/genres');
      } catch (error) {
        console.error('Error deleting genre:', error);
      }
    }
  };

  return (
    <div>
      <h2>{id ? 'Edit Genre' : 'Create Genre'}</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" value={genre.name} onChange={handleChange} placeholder="Genre Name" required />
        <button type="submit">{id ? 'Update' : 'Create'}</button>
        {id && <button type="button" onClick={handleDelete}>Delete</button>}
      </form>
    </div>
  );
}

export default AdminGenreForm;
